import React, { useState } from 'react';
import { ChakraProvider, Box, VStack, Heading, Image, Text, Link, useToast } from '@chakra-ui/react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

function App() {
  const [qrCode, setQrCode] = useState(null);
  const [shareableLink, setShareableLink] = useState(null);
  const [uploading, setUploading] = useState(false);
  const toast = useToast();

  const onDrop = async (acceptedFiles) => {
    if (acceptedFiles.length === 0) return;

    const file = acceptedFiles[0];
    const formData = new FormData();
    formData.append('file', file);

    setUploading(true);
    try {
      const response = await axios.post('http://localhost:3001/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setQrCode(response.data.qrCode);
      setShareableLink(response.data.shareableLink);

      toast({
        title: 'File uploaded successfully!',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: 'Please try again',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setUploading(false);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    multiple: false,
  });

  return (
    <ChakraProvider>
      <Box minH="100vh" bg="gray.50" py={10}>
        <VStack spacing={8} maxW="container.md" mx="auto" px={4}>
          <Heading>QR Share</Heading>

          <Box
            {...getRootProps()}
            w="full"
            h="200px"
            border="2px dashed"
            borderColor={isDragActive ? "blue.400" : "gray.200"}
            borderRadius="lg"
            display="flex"
            alignItems="center"
            justifyContent="center"
            bg="white"
            transition="all 0.2s"
            _hover={{ borderColor: "blue.400" }}
            cursor="pointer"
          >
            <input {...getInputProps()} />
            <VStack spacing={2}>
              <Text color="gray.500">
                {uploading
                  ? "Uploading..."
                  : isDragActive
                  ? "Drop the file here"
                  : "Drag and drop a file here, or click to select"}
              </Text>
            </VStack>
          </Box>

          {qrCode && (
            <VStack spacing={4} bg="white" p={6} borderRadius="lg" shadow="md" w="full">
              <Image src={qrCode} alt="QR Code" boxSize="200px" />
              <Text>Scan QR code or use link below:</Text>
              <Link href={shareableLink} color="blue.500" isExternal>
                {shareableLink}
              </Link>
            </VStack>
          )}
        </VStack>
      </Box>
    </ChakraProvider>
  );
}

export default App;
